mapper.insert:
INSERT INTO projet.commande(id_commande, cip, date_commande, status, Nom, Prenom)
VALUES (#{commande.idCommande}, #{commande.cip}, #{commande.dateCommande}, #{commande.status}, #{commande.Nom}, #{commande.Prenom})
    ON CONFLICT (id_commande) DO NOTHING;

mapper.select:
SELECT id_commande AS idCommande, cip, date_commande AS dateCommande, status, Nom, Prenom
FROM projet.commande
WHERE id_commande = #{idCommande}

SELECT cip, Nom AS lastName, Prenom AS firstName, Courriel AS email, role
FROM projet.Utilisateur
WHERE cip = #{cip}

mapper.update:
UPDATE projet.commande
SET status = 'terminee'
WHERE id_commande = #{idCommande}

mapper.select:
SELECT id_produit AS idProduit, Nom AS nomProduit, Prix AS PrixProduit
FROM projet.Produit
WHERE id_produit = #{idProduit}
